import itertools

def compare(x,y):
    if x==y:
        return 0
    elif (x=='r' and y=='s') or (x=='s' and y=='p') or (x=='p' and y=='r'):
        return 1
    else:
        return -1

def minmax(a,b):
    min = 0
    max = 0
    for x,y in zip(a,b):
        c = compare(x,y)
        if c==1:
            max += 1
        elif c==-1:
            min += 1
    return [min,max]

def main():
    n = int(input())
    a = [int(i) for i in input().split()]
    b = [int(i) for i in input().split()]
    a = list(itertools.chain.from_iterable([['r']*a[0],['s']*a[1],['p']*a[2]]))
    b = list(itertools.chain.from_iterable([['r']*b[0],['s']*b[1],['p']*b[2]]))
    a.sort()
    b.sort()
    print(" ".join([str(i) for i in minmax(a,b)]))

