def solve(arr):
    s = sum(arr)
    c = 0
    for i in arr:
        if i % 2 == 0:
            c += 1
    return s - c

print(solve(list(map(int, input().split()))))

from math import gcd
from functools import reduce


def lcm(a, b):
    return a * b // gcd(a, b)


print(reduce(lcm, map(int, input().split())))

def main():
