n = int(input())

def solution(n):
    cnt = 0
    for a in range(1, n):
        for b in range(a, n):
            c = n - a * b
            if c < 1:
                continue
            if c < b:
                break
            if a * b + c == n:
                cnt += 1
    return cnt

print(solution(n))

