        print(max(abs(a), b) * max(abs(c), d))
    elif a <= 0 and b >= 0:
        print(max(abs(a), b) * d)
    elif c <= 0 and d >= 0:
        print(a * max(abs(c), d))
    else:
        print(a * d)

