def lucky(num):
    num=str(num)
    if len(num)==1:
        if num[0] in ['4','7']:
            return 1
        else:
            return 0
    else:
        if num[0] in ['4','7'] and num[1] in ['4','7']:
            return 1
        else:
            return 0

print(lucky(int(input())))

def is_lucky(n):
    n = str(n)
    return sum([int(n[i]) for i in range(3)]) == sum([int(n[i]) for i in range(3,6)])

print(is_lucky(int(input())))

import math

n = int(input())

def is_prime(n):
    if n == 1:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

print(1 if is_prime(n) else 0)
