import sys

def main():
    n,k,m = map(int,sys.stdin.readline().split())
    a = list(map(int,sys.stdin.readline().split()))
    total = sum(a)
    x = (m*n)-total
    if x<0:
        print(-1)
    elif x>k:
        print(-1)
    else:
        print(x)

if __name__ == "__main__":
    main()

