def judge_strong_palindrome(input_str):
    if input_str == input_str[::-1]:
        if input_str[:len(input_str) // 2] == input_str[:len(input_str) // 2][::-1]:
            if input_str[(len(input_str) + 3) // 2:] == input_str[(len(input_str) + 3) // 2:][::-1]:
                return True
    return False


def main():
    input_str = str(input())
    if judge_strong_palindrome(input_str):
        print("Yes")
    else:
        print("No")


if __name__ == '__main__':
    main()

import sys

def is_palindrome(str):
    # print('str:', str)
    if len(str) == 1:
        return True
    elif str[0] == str[-1]:
        return is_palindrome(str[1:-1])
    else:
        return False

if __name__ == '__main__':
    s = sys.stdin.readline().rstrip()
    # print('s:', s)
    if len(s) % 2 == 0:
        print('No')
        sys.exit()
    if len(s) == 1:
        print('Yes')
        sys.exit()
    if is_palindrome(s) == False:
        print('No')
        sys.exit()
    if is_palindrome(s[0:(len(s)-1)//2]) == False:
        print('No')
        sys.exit()
    if is_palindrome(s[(len(s)+3)//2-1:]) == False:
        print('No')
        sys.exit()
    print('Yes')
