import math

n = int(input())
a = list(map(int, input().split()))

count = {}

for i in a:
    if i in count:
        count[i] += 1
    else:
        count[i] = 1

ans = math.inf

for i in count:
    if count[i] >= 2:
        ans = min(ans, count[i] - 2)

if ans == math.inf:
    print(len(a) // 2)
else:
    print(len(a) // 2 - ans)

