from collections import Counter

n = int(input())
arr = list(map(int, input().split()))
c = Counter(arr)
if len(c) != 2:
    print(n)
elif arr[0] == arr[-1]:
    print(n-c[arr[0]])
else:
    print(n-min(c[arr[0]], c[arr[-1]]))

