import sys

def read_input():
    return sys.stdin.readline().strip()

def main():
    s = read_input()
    count_red = 0
    count_blue = 0
    max_cubes = 0
    for c in s:
        if c == '0':
            count_red += 1
        elif c == '1':
            count_blue += 1
        if count_red == count_blue:
            max_cubes = count_red * 2
    print(max_cubes)

if __name__ == "__main__":
    main()

def main():
    s = input()
    ans = 0
    stack = []
    for c in s:
        if c == "0":
            stack.append(c)
        else:
            if len(stack) > 0:
                stack.pop()
                ans += 2
    print(ans)

if __name__ == "__main__":
    main()

import sys

s = sys.stdin.readline().rstrip('\n')

red = 0
blue = 0

for c in s:
    if c == '0':
        red += 1
    else:
        blue += 1

print(min(red, blue) * 2)

str_input = input()

if '1' not in str_input:
    print(len(str_input))
else:
    count = 0
    for i in range(len(str_input)):
        if str_input[i] == '1':
            count += 1
            break
    for i in range(len(str_input)-1, -1, -1):
        if str_input[i] == '0':
            count += 1
            break
    print(count)
