n = int(input())
h = list(map(int, input().split()))

# h[i]がi+1より大きくなる回数を数える
count = 0
for i in range(n):
    if h[i] > i+1:
        count += h[i] - (i+1)

# h[i]がi+1未満になる回数を数える
count2 = 0
for i in range(n):
    if h[i] < i+1:
        count2 += (i+1) - h[i]

print(max(count, count2))

N = int(input())
h = list(map(int, input().split()))

count = 0
maxH = 0
for i in range(N):
    if h[i] < maxH:
        continue
    elif h[i] == maxH:
        count += 1
    else:
        count += h[i] - maxH
        maxH = h[i]

print(count)

def main():
    n = int(input())
    heights = list(map(int, input().split()))
    desired_heights = sorted(heights)
    ans = 0
    for i in range(n):
        if heights[i] != desired_heights[i]:
            ans += 1
    print(ans)


if __name__ == '__main__':
    main()

