def find_moves(n, w, moves):
    start_positions = []

    for i in range(0, w + 1):
        current_position = i
        can_move = True

        for move in moves:
            current_position += move

            if current_position > w or current_position < 0:
                can_move = False
                break

        if can_move:
            start_positions.append(i)

    return len(start_positions)


