import sys

def possible_solutions(n,w,a):
    #print(f'n={n} w={w} a={a}')
    if n == 0:
        return 1
    if n == 1:
        #print(f'a[0]={a[0]}')
        if (w - a[0]) >= 0 and (w - a[0]) <= w:
            return 1
        return 0
    total = 0
    for i in range(0, w+1):
        if (i - a[0]) >= 0 and (i - a[0]) <= w:
            total += possible_solutions(n-1, w, a[1:])
    return total

def main():
    n, w = map(int, sys.stdin.readline().split())
    a = list(map(int, sys.stdin.readline().split()))
    print(possible_solutions(n,w,a))

