# Longest Common Divisor
def gcd(a, b):
    return a if b == 0 else gcd(b, a % b)

# Least Common Multiple
def lcm(a, b):
    return (a * b) // gcd(a, b)

def get_max_f(n, a):
    # lcm of all a_i
    l = a[0]
    for i in range(1, n):
        l = lcm(l, a[i])

    # sum of all a_i
    s = sum(a)

    # maximum value of f
    return (l - 1) * s // l

