import sys

def main():
    n = int(sys.stdin.readline())
    ls = []
    for i in range(n):
        ls.append(int(sys.stdin.readline()))
    ls.sort()
    if ls[n-1] < sum(ls[:n-1]):
        print('Yes')
    else:
        print('No')

if __name__ == '__main__':
    main()

