def main():
    n = int(input())
    ans = ""
    while n != 0:
        if n % 2 == 0:
            ans += "0"
        else:
            n -= 1
            ans += "1"
        n //= -2
    if ans == "":
        ans = "0"
    else:
        ans = ans[::-1]
    print(ans)

if __name__ == "__main__":
    main()

import sys

def convert(val):
    if val == 0:
        return '0'
    result = ''
    while val != 0:
        val, rem = divmod(val, -2)
        if rem < 0:
            val, rem = val + 1, rem + 2
        result = str(rem) + result
    return result

if __name__ == '__main__':
    val = int(sys.stdin.readline().rstrip())
    print(convert(val))

import sys

input = sys.stdin.readline

def main():
    N = int(input())
    S = []
    while N != 0:
        S.append(str(N % 2))
        N = (N - N % 2) // -2
    print("".join(reversed(S)))

if __name__ == '__main__':
    main()

