# 参考：https://qiita.com/drken/items/3b4fdf0a78e7a138cd9a

def main():
    input_num = int(input())

    if input_num == 0:
        print(0)
        return

    base_minus_two = []
    while input_num != 0:
        base_minus_two.append(input_num % -2)
        input_num = input_num // -2

    base_minus_two.reverse()
    print(''.join(map(str, base_minus_two)))


if __name__ == '__main__':
    main()
