def main():
    n = int(input())
    monsters = list(map(int, input().split()))
    heroes = list(map(int, input().split()))

    max_monsters = 0
    for i, hero in enumerate(heroes):
        if i == 0:
            max_monsters += min(hero, monsters[i] + monsters[i+1])
        elif i == len(heroes) - 1:
            max_monsters += min(hero, monsters[i] + monsters[i+1])
        else:
            max_monsters += min(hero, monsters[i] + monsters[i+1] - monsters[i-1])

    print(max_monsters)

