import sys

def main():
    N = int(input())
    A = list(map(int, input().split()))
    B = list(map(int, input().split()))
    C = [0] * (N+1)

    for i in range(N):
        if A[i] >= B[i]:
            C[i] = B[i]
        else:
            C[i] = A[i]
            C[i+1] = B[i] - A[i]
    print(sum(C))

if __name__ == "__main__":
    main()
