N = int(input())
W = list(map(int, input().split()))

sum_of_all = sum(W)
dp = [0 for _ in range(sum_of_all+1)]
dp[0] = 1
for w in W:
    for i in range(sum_of_all, -1, -1):
        if dp[i] == 1:
            dp[i+w] = 1

ans = sum_of_all
for i in range(sum_of_all//2+1):
    if dp[i] == 1:
        ans = min(ans, abs(sum_of_all-2*i))
print(ans)

N = int(input())
W = list(map(int, input().split()))

dp = [[0 for _ in range(N+1)] for _ in range(N+1)]

for i in range(N+1):
    for j in range(N+1):
        if i == 0 or j == 0:
            dp[i][j] = 0
        else:
            dp[i][j] = max(dp[i-1][j], dp[i-1][j-1] + W[i-1])

print(dp[N][N//2] - dp[N][N//2+1])

def main():
    n = int(input())
    w = list(map(int, input().split()))
    ans = float("inf")
    for i in range(1, n):
        ans = min(ans, abs(sum(w[:i]) - sum(w[i:])))
    print(ans)


if __name__ == "__main__":
    main()
