def solve(n, a):
    a.sort()
    ans = 0
    for i in range(0, n, 2):
        ans += a[i + 1] - a[i]
    return ans


def main():
    n = int(input())
    a = [int(i) for i in input().split()]
    print(solve(n, a))

if __name__ == '__main__':
    main()

def read_int():
    return int(input())

def read_int_list():
    return [int(s) for s in input().split(" ")]

def solve_problem(n, a):
    a = sorted(a)
    sum = 0
    for i in range(0, n, 2):
        sum += a[i+1] - a[i]
    return sum

def main():
    n = read_int()
    a = read_int_list()
    print(solve_problem(n, a))

if __name__ == "__main__":
    main()

from collections import defaultdict

n = int(input())
a = [int(x) for x in input().split()]

d = defaultdict(int)
for i in a:
    d[i] += 1

ans = 0
for i in d.values():
    if i > 2:
        ans += i - 2
    if i == 1:
        ans += 1

print(ans)
