import sys

def main():
    input_list = sys.stdin.readline().split()
    divisible = int(input_list[0])
    n = int(input_list[1])

    if divisible == 0:
        print(n)
    elif divisible == 1:
        print(100 * (n-1) + 1)
    else:
        print(10000 * (n-1) + 1)

