import sys

def main():
    s = input()
    k = int(input())
    ans = ""
    for i in range(len(s)):
        if i == 0:
            ans += s[i]
        else:
            ans += s[i] * (i + 1)
    print(ans[k - 1])

if __name__ == "__main__":
    main()

