import sys

def main():
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))
    max_diff = 0
    for i in range(n):
        for j in range(n):
            if i == j:
                continue
            else:
                diff = abs(a[i] - a[j])
                if diff > max_diff:
                    max_diff = diff
    print(max_diff)

if __name__ == "__main__":
    main()
