def main():
    N, M = map(int, input().split())
    liked_foods = {}
    for i in range(1, M+1):
        liked_foods[i] = 0

    for i in range(N):
        K = int(input().split()[0])
        foods = map(int, input().split())
        for food in foods:
            liked_foods[food] += 1

    count = 0
    for key, value in liked_foods.items():
        if value == N:
            count += 1

    print(count)

if __name__ == '__main__':
    main()

