def main():
    N = int(input())
    odd_count = 0
    for i in range(1, N+1):
        if i % 2 == 1:
            odd_count += 1
    print(odd_count / N)


if __name__ == '__main__':
    main()

def calculate_probability(N):
    even_count = 0
    for i in range(1, N+1):
        if i % 2 == 0:
            even_count += 1
    return 1 - even_count/N

if __name__ == '__main__':
    N = int(input())
    print(calculate_probability(N))

def main():
    n = int(input())

    # 奇数の個数を求める
    odd_num = 0
    for i in range(1, n+1):
        if i%2 != 0:
            odd_num += 1

    # 奇数の割合を出力
    print(odd_num/n)

if __name__ == '__main__':
    main()

N = int(input())

if N % 2 == 1:
