import sys

def main():
    # Read lines from stdin
    lines = sys.stdin.readlines()

    # Parse input
    x, y = map(int, lines[0].split(' '))

    # Check if solution possible
    if x == y/2 or y == 2*x:
        print('Yes')
    else:
        print('No')

if __name__ == '__main__':
    main()

