def is_nine_multiple(n: int) -> bool:
    """
    nが9の倍数か判定する
    """
    while True:
        # nが1桁になったら、終了
        if n < 10:
            break
        # nが1桁になるまで、各桁の和を計算し続ける
        n = sum(int(i) for i in str(n))

    # 各桁の和が9の倍数ならTrue、そうでなければFalseを返す
    return n % 9 == 0


def main():
    n = int(input())
    if is_nine_multiple(n):
        print('Yes')
    else:
        print('No')


