import sys

def solve(A, B):
    ans = 0
    while B-A > 0:
        ans += 1
        B -= A
    return ans+1

def main():
    line = sys.stdin.readline()
    A, B = map(int, line.strip().split())
    print(solve(A, B))

if __name__ == '__main__':
    main()

