import sys

def main():
    line = sys.stdin.readline()
    A, B = map(int, line.split())
    cnt = 0
    while B > 0:
        if B >= A:
            cnt += 1
            B -= A
        else:
            A = B
            cnt += 1
            B -= A
    print(cnt)

if __name__ == "__main__":
    main()
