def main():
    n = int(input())
    x = list(map(int, input().split()))

    mn = 0
    mx = 0

    curr = x[0]
    for i in range(1, n):
        if x[i] == curr:
            mn += 1
        elif x[i] > curr:
            curr = x[i]
        else:
            curr = x[i] + 1
            mn += 1

    curr = x[0]
    for i in range(1, n):
        if x[i] == curr:
            mx += 1
        elif x[i] > curr:
            curr = x[i] + 1
        else:
            curr = x[i]
            mx += 1

    print(mn, mx)


if __name__ == "__main__":
    main()

def check(n, arr):
    s = set()
    for i in range(len(arr)):
        if arr[i] == 1:
            s.add(arr[i])
            s.add(arr[i] + 1)
        elif arr[i] == n:
            s.add(arr[i] - 1)
            s.add(arr[i])
        else:
            s.add(arr[i] - 1)
            s.add(arr[i])
            s.add(arr[i] + 1)
    return len(s)


if __name__ == "__main__":
    n = int(input())
    arr = [int(x) for x in input().split()]
    print(check(n, arr), check(n, [1] * n))
