def replace(a, f):
    d = {}
    for i in range(1, 10):
        d[i] = f[i-1]
    new_a = []
    for i in a:
        new_a.append(d[int(i)])
    return new_a

def max_number(a, f):
    max_num = 0
    for i in range(n):
        for j in range(i+1, n+1):
            new_a = replace(a[i:j], f)
            new_a = int(''.join(map(str, new_a)))
            if new_a > max_num:
                max_num = new_a
    return max_num

