from collections import Counter
from typing import List

def solve(a: List[int], f: List[int]) -> int:
    c = Counter(a)
    for i in range(9, 0, -1):
        if c[i] >= c[f[i-1]]:
            return a
    s = []
    for d in a:
        if c[f[d-1]] > 0:
            s.append(f[d-1])
            c[f[d-1]] -= 1
        else:
            s.append(d)
    return int(''.join(map(str, s)))

def main() -> None:
    n = int(input())
    a = list(map(int, input()))
    f = list(map(int, input().split()))
    print(solve(a, f))

if __name__ == '__main__':
    main()
