def solver(string, count):
    if count == 1:
        return string

    string_length = len(string)
    reoccurrence_index = 0
    for i in range(1, string_length):
        if string[i] == string[0]:
            reoccurrence_index = i
            break
    else:
        return string * count

    divisor = gcd(reoccurrence_index, string_length)
    # print(divisor)
    if count % divisor == 0:
        return string * (count // divisor)

    return solver(string, count - (count % divisor)) + string[:count % divisor]


