def main():
    n = int(input())
    a = [int(i) for i in input().split()]
    s = sum(a)
    res = []
    for i in range(n):
        if 2 * a[i] == s:
            res.append(i + 1)
    print(len(res))
    print(*res)


if __name__ == '__main__':
    main()

def main():
	n = int(input())
	a = list(map(int, input().split()))
	left, right = 0, sum(a)
	for i in range(n):
		right -= a[i]
		if left == right:
			print(i + 1, end=' ')
		left += a[i]

if __name__ == '__main__':
	main()
