def main():
    n = int(input())
    x = list(map(int, input().split()))
    if n > 1:
        min_x = min(x)
        max_x = max(x)
        print(max_x - min_x)
    else:
        print(0)


if __name__ == "__main__":
    main()

n = int(input())
x = [int(i) for i in input().split()]

c = 0

for i in x:
  if i % 2 == 0:
    c += i//2
  else:
    c += (i+1)//2

print(c)

n = int(input())
x = list(map(int, input().split()))

x.sort()

if n % 2 == 0:
    if x[n//2] == x[n//2 - 1]:
        print(0)
    else:
        print(x[n//2] - x[n//2 - 1])
else:
    print(0)
