import math

N = int(input())

p = int(math.sqrt(N))

if N == p*p:
    print(p-1)
else:
    n = N - p*p
    if n <= p+1:
        print(2*p-1)
    else:
        print(2*p)

def main():
    n = int(input())
    #print(n)
    count = 0
    x = 1
    y = 1
    while(True):
        if(n == x):
            break
        elif(n == y):
            break
        elif(n < x):
            x -= 1
            count += 1
        elif(n < y):
            y -= 1
            count += 1
        else:
            if(x > y):
                y += 1
                count += 1
            else:
                x += 1
                count += 1
    print(count)

main()

