import sys
import collections


def solve():
    n = int(input())
    problems = list(map(int, input().split()))

    counter = collections.Counter(problems)

    result = 0
    for k, v in counter.items():
        if k > n // 2:
            result += v
    return result


def main():
    print(solve())


if __name__ == "__main__":
