N = int(input())
d_list = list(map(int, input().split()))

d_list.sort(reverse=True)

d_list = [0] + d_list

# dp[i][j] = dp[i-1][j] + dp[i-1][j-d_list[i]]
# dp[i][j] = dp[i-1][j] + dp[i-1][j-d_list[i]]
dp = [[0 for _ in range(N+1)] for _ in range(N+1)]

for i in range(N+1):
    dp[0][i] = 1

for i in range(1, N+1):
    for j in range(N+1):
        dp[i][j] = dp[i-1][j]
        if j - d_list[i] >= 0:
            dp[i][j] += dp[i-1][j-d_list[i]]

ans = 0
for i in range(1, N//2+1):
    if dp[N//2][i] > 0:
        ans += 1

print(ans)
