import sys

def main():
    input = sys.stdin.readline().strip()
    nums = map(int, sys.stdin.readline().strip().split())
    print solve(nums)

def solve(nums):
    i = 0
    nums.sort()
    for num in nums:
        if i + 1 < num:
            break
        i += 1
    return i + 1

