import sys

def isDiverse(s):
    """Returns whether a string is diverse"""
    seen = set()
    for c in s:
        if c in seen:
            return False
        seen.add(c)
    # Check if the string is contiguous
    for i in range(len(s) - 1):
        if ord(s[i]) + 1 != ord(s[i + 1]):
            return False
    return True

def main():
    """Main function"""
    n = int(sys.stdin.readline())
    for _ in range(n):
        s = sys.stdin.readline().strip()
        if isDiverse(s):
            print("Yes")
        else:
            print("No")

