n,a,b = map(int,input().split())

def solve(n,a,b):
    if a > b:
        a,b = b,a
    if a == b:
        return n // (a + b) * a + min(n % (a + b), a)
    # 全部赤
    if n <= a:
        return 0
    # 全部青
    if (a + b) * (n // (a + b)) >= n:
        return n - a
    # 中間
    return n // (a + b) * a + min(n % (a + b), a)

print(solve(n,a,b))
