for _ in range(int(input())):
    n, m = map(int, input().split())
    if n == 1:
        print(0)
        continue
    if m < n:
        print(m)
        continue
    if m % n == 0:
        print(m - m // n)
    else:
        print(m - m // n - 1)

def main():
    for _ in range(int(input())):
        n, m = map(int, input().split())
        print(min(m, 2 * m - n))


if __name__ == '__main__':
    main()

