import sys

def main():
    t = int(sys.stdin.readline())
    for _ in range(t):
        n, m = map(int, sys.stdin.readline().split())
        if n == 1:
            print(0)
        else:
            print((m - n + 1) * 2)

if __name__ == '__main__':
    main()
