def is_good(n):
    s = str(n)
    a = int(s[0])
    b = int(s[-1])
    if a == 0 or b == 0:
        return False
    s = str(a*b)
    if len(s) != len(set(s)):
        return False
    return True

def main():
    n = int(input())
    if is_good(n):
        print("Yes")
    else:
        print("No")

if __name__ == "__main__":
    main()

