def is_lucky(num):
    if len(num) <= 1:
        return False

    divisor = len(num) // 2
    sum_left = sum([int(char) for char in num[:divisor]])
    sum_right = sum([int(char) for char in num[divisor:]])

    return sum_left == sum_right


def main():
    num = input()
    print("Yes" if is_lucky(num) else "No")


if __name__ == '__main__':
    main()
