import string

def wifi_range_room(length, room_range, room_spot):
	#initialize the rooms dictionary
	room_dict = {}
	for i in range(1, length + 1):
		room_dict[i] = 0

	#place the routers
	for i in range(1, length + 1):
		if room_spot[i -1] == '1':
			for j in range(max(1, i - room_range), min(length, i + room_range) + 1):
				room_dict[j] = 1

	#count the cost
	cost = 0
	for i in range(1, length + 1):
		if room_dict[i] == 0:
			cost += i
	return cost

