N = int(input())

def to_list(n):
    return list(map(int, list(str(n))))

def is_shichigosan(n):
    if n < 357:
        return False
    num = to_list(n)
    if not 7 in num or not 5 in num or not 3 in num:
        return False
    for i in range(10):
        if i == 7 or i == 5 or i == 3:
            continue
        if i in num:
            return False
    return True

count = 0
for i in range(1, N+1):
    if is_shichigosan(i):
