import math

def is_prime(n):
    if n == 2:
        return True
    elif n < 2 or n % 2 == 0:
        return False
    for i in range(3, int(math.sqrt(n)) + 1, 2):
        if n % i == 0:
            return False
    return True

X = int(input())
if is_prime(X):
    print(X)
else:
    for i in range(X, X + 100000, 1):
        if is_prime(i):
            print(i)
            break

