def solve(n, a, b):
    if a < b:
        a, b = b, a
    if n % 2 == 0:
        return n * b // 2
    else:
        return b + (n - 1) * b // 2

q = int(input())

for _ in range(q):
    n, a, b = map(int, input().split())
    print(solve(n, a, b))

